package primeros1000;

public class primeros1000 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/* declaramos la variable numero que contiene el número 0*/
int numero = 0;
/* después creamos un bucle donde siempre que numero sea menor que 1000, 
  se imprimirá número por pantalla hasta que numero sea mayor o igual que 10000*/
while (numero < 10000)
{
	System.out.println(numero);
	numero++;
	}

}
}
